// Contenido para src/components/Sidebar/Sidebar.tsx

import React from 'react';
// Usamos NavLink en lugar de Link para que sepa cu√°l es la "p√°gina activa"
import { NavLink } from 'react-router-dom'; 

// Definimos los props que recibir√° (el rol del usuario actual)
type SidebarProps = {
  role: string;
};

// Definimos un tipo para los enlaces
type NavItem = {
  path: string;
  text: string;
  icon: string;
};

const Sidebar: React.FC<SidebarProps> = ({ role }) => {
  let navItems: NavItem[] = [];

  // Esta l√≥gica es la misma de tu index.html, pero adaptada a React
  switch (role) {
    case 'student':
      navItems = [
        { text: 'Mi Perfil', icon: 'üë§', path: '/dashboard' },
        { text: 'Mis Notas', icon: 'üìä', path: '/grades' },
        { text: 'Mi Asistencia', icon: 'üìÖ', path: '/attendance' },
        { text: 'Mensajes', icon: 'üí¨', path: '/messages' },
        { text: 'Configuraci√≥n', icon: '‚öôÔ∏è', path: '/settings' }
      ];
      break;
    case 'psychologist':
      navItems = [
        { text: 'Panel Principal', icon: 'üß†', path: '/dashboard' },
        { text: 'Estudiantes en Riesgo', icon: '‚ö†Ô∏è', path: '/risk' },
        { text: 'Agenda', icon: 'üìÖ', path: '/schedule' },
        { text: 'Reportes', icon: 'üìà', path: '/reports' },
        { text: 'Mensajer√≠a', icon: 'üí¨', path: '/messages' },
        { text: 'Configuraci√≥n', icon: '‚öôÔ∏è', path: '/settings' }
      ];
      break;
    case 'director':
      navItems = [
        { text: 'Panel Directivo', icon: 'üëî', path: '/dashboard' },
        { text: 'Indicadores', icon: 'üìä', path: '/metrics' },
        { text: 'Reportes Ejecutivos', icon: 'üìà', path: '/reports' },
        { text: 'Mensajer√≠a', icon: 'üí¨', path: '/messages' },
        { text: 'Configuraci√≥n', icon: '‚öôÔ∏è', path: '/settings' }
      ];
      break;
    default: // 'teacher'
      navItems = [
        { text: 'Dashboard', icon: 'üìä', path: '/dashboard' },
        { text: 'Administraci√≥n', icon: 'üèõÔ∏è', path: '/admin' },
        { text: 'Mis Clases', icon: 'üìö', path: '/classes' },
        { text: 'Asistencia', icon: 'üè´', path: '/attendance' },
        { text: 'Reportes', icon: 'üìà', path: '/reports' },
        { text: 'Estudiantes', icon: 'üë•', path: '/students' },
        { text: 'Mensajer√≠a', icon: 'üí¨', path: '/messages' },
        { text: 'Configuraci√≥n', icon: '‚öôÔ∏è', path: '/settings' }
      ];
  }

  return (
    <nav className="sidebar">
      <div className="sidebar-header">
        <div className="sidebar-logo">üìö</div>
        <div className="sidebar-title">Men√∫</div>
      </div>
      <div className="nav-items-container">
        {navItems.map((item, index) => (
          <NavLink 
            to={item.path} 
            key={item.path}
            className={({ isActive }) => `nav-item ${isActive ? "active" : ""}`}
            style={{ animationDelay: `${index * 0.05}s` }}
          >
            <div className="nav-icon-wrapper">
              <span className="nav-icon">{item.icon}</span>
            </div>
            <span className="nav-text">{item.text}</span>
            <div className="nav-indicator"></div>
          </NavLink>
        ))}
      </div>
    </nav>
  );
};

export default Sidebar;
