import React, { useState, useEffect } from 'react';
import { 
  generarReporteAsistencia, 
  generarReporteAcademico,
  obtenerReportesGuardados,
  ReporteGenerado 
} from '../../services/reporteService';

const ReportsSystem: React.FC = () => {
  const [usuarioActual, setUsuarioActual] = useState<any>(null);
  const [gradosDisponibles, setGradosDisponibles] = useState<string[]>([
    '1ro Medio',
    '2do Medio',
    '3ro Medio',
    '4to Medio'
  ]);
  const [seccionesDisponibles, setSeccionesDisponibles] = useState<string[]>(['A', 'B', 'C']);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [reporteGenerado, setReporteGenerado] = useState<any>(null);
  const [reportesGuardados, setReportesGuardados] = useState<ReporteGenerado[]>([]);
  const [mostrandoReporte, setMostrandoReporte] = useState(false);

  const [formData, setFormData] = useState({
    tipo: 'asistencia' as 'asistencia' | 'academico',
    grado: '',
    seccion: '',
    fechaInicio: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    fechaFin: new Date().toISOString().split('T')[0]
  });

  useEffect(() => {
    cargarReportesGuardados();
    cargarUsuario();
  }, []);

  const cargarUsuario = () => {
    const usuarioGuardado = localStorage.getItem('usuario');
    if (!usuarioGuardado) return;

    try {
      const usuario = JSON.parse(usuarioGuardado);
      setUsuarioActual(usuario);

      // limitar opciones de grado y secciÃ³n a las asignadas al docente
      if (usuario.rol === 'docente' && usuario.detalles?.grados_array?.length > 0) {
        const gradosArray: string[] = usuario.detalles.grados_array;

        const gradesSet = new Set<string>();
        const sectionsSet = new Set<string>();

        gradosArray.forEach((item) => {
          const match = (item as string).match(/(.+)\s+([A-Z])$/);
          if (match) {
            gradesSet.add(match[1]);
            sectionsSet.add(match[2]);
          }
        });

        if (gradesSet.size > 0) setGradosDisponibles(Array.from(gradesSet));
        if (sectionsSet.size > 0) setSeccionesDisponibles(Array.from(sectionsSet));
      }
    } catch (err) {
      console.error('Error al cargar usuario:', err);
    }
  };

  const cargarReportesGuardados = async () => {
    try {
      const reportes = await obtenerReportesGuardados();
      setReportesGuardados(reportes);
    } catch (err: any) {
      console.error('Error cargando reportes:', err);
    }
  };

  const handleGenerarReporte = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setMostrandoReporte(true);

    try {
      let reporte;
      const gradosPermitidos = usuarioActual?.rol === 'docente' ? gradosDisponibles : undefined;
      const seccionesPermitidas = usuarioActual?.rol === 'docente' ? seccionesDisponibles : undefined;
      
      if (formData.tipo === 'asistencia') {
        reporte = await generarReporteAsistencia(
          formData.fechaInicio,
          formData.fechaFin,
          formData.grado || undefined,
          formData.seccion || undefined,
          gradosPermitidos,
          seccionesPermitidas
        );
      } else {
        reporte = await generarReporteAcademico(
          formData.grado || undefined,
          formData.seccion || undefined,
          gradosPermitidos,
          seccionesPermitidas
        );
      }

      setReporteGenerado(reporte);
    } catch (err: any) {
      setError('Error al generar reporte: ' + err.message);
      setMostrandoReporte(false);
    } finally {
      setLoading(false);
    }
  };

  const renderReporteAsistencia = (reporte: any) => {
    if (!reporte) return null;

    return (
      <div>
        {/* EstadÃ­sticas Generales */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', 
          gap: '16px', 
          marginBottom: '24px' 
        }}>
          <div style={{ 
            backgroundColor: '#f0fdf4', 
            padding: '20px', 
            borderRadius: '12px',
            border: '1px solid #bbf7d0'
          }}>
            <div style={{ fontSize: '28px', marginBottom: '8px' }}>âœ“</div>
            <div style={{ fontSize: '24px', fontWeight: '700', color: '#16a34a' }}>
              {reporte.estadisticasGenerales.presente}
            </div>
