                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                  gap: '16px',
                  marginBottom: '20px'
                }}>
                  <div>
                    <label style={labelStyle}>Nombre *</label>
                    <input
                      type="text"
                      placeholder="Ej: MatemÃ¡ticas Avanzadas"
                      value={nuevaMateria.nombre}
