                </p>
              ) : (
                estudiantesDisponibles.map((estudiante) => {
                  const estaInscrito = estudiantesInscritos.includes(estudiante.id);
                  return (
                    <div
                      key={estudiante.id}
                      onClick={() => toggleInscripcionEstudiante(estudiante.id)}
