                        padding: '12px',
                        borderRadius: '8px',
                        cursor: 'pointer',
                        backgroundColor: estaInscrito ? '#dbeafe' : 'transparent',
                        border: estaInscrito ? '2px solid #3b82f6' : '1px solid #e5e7eb',
