              <button
                onClick={() => setModalEdicion({ abierto: false, clase: null })}
                style={{
                  backgroundColor: 'transparent',
                  border: 'none',
                  fontSize: '24px',
                  cursor: 'pointer',
                  color: '#6b7280'
                }}
              >
                âœ•
              </button>
            </div>

            <form onSubmit={handleEditarClase}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
                <div>
                  <label style={labelStyle}>Grado *</label>
                  <select
                    value={nuevaClase.grado}
                    onChange={(e) => setNuevaClase({ ...nuevaClase, grado: e.target.value })}
                    style={inputStyle}
                    required
                  >
                    <option value="">Seleccionar grado</option>
                    {(docenteActual.detalles?.grados_array || []).map((gradoSeccion: string) => {
                      const grado = gradoSeccion.split(/(?=[A-Z])/)[0];
                      return (
                        <option key={grado} value={grado}>
                          {grado}
                        </option>
                      );
                    }).filter((v: any, i: number, a: any[]) => a.indexOf(v) === i)}
                  </select>
                </div>

                <div>
                  <label style={labelStyle}>SecciÃ³n *</label>
                  <select
                    value={nuevaClase.seccion}
                    onChange={(e) => setNuevaClase({ ...nuevaClase, seccion: e.target.value })}
                    style={inputStyle}
                    required
                    disabled={!nuevaClase.grado}
                  >
                    <option value="">Seleccionar secciÃ³n</option>
                    {nuevaClase.grado && (docenteActual.detalles?.grados_array || [])
                      .filter((gs: string) => gs.startsWith(nuevaClase.grado))
                      .map((gs: string) => {
                        const seccion = gs.replace(nuevaClase.grado, '');
                        return (
                          <option key={seccion} value={seccion}>
                            {seccion}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <div>
                  <label style={labelStyle}>Horario</label>
                  <input
                    type="text"
                    placeholder="Ej: Lunes 8:00-9:30, MiÃ©rcoles 8:00-9:30"
                    value={nuevaClase.horario}
                    onChange={(e) => setNuevaClase({ ...nuevaClase, horario: e.target.value })}
                    style={inputStyle}
                  />
                </div>

                <div>
                  <label style={labelStyle}>Aula</label>
                  <input
                    type="text"
                    placeholder="Ej: Aula 101"
                    value={nuevaClase.aula}
                    onChange={(e) => setNuevaClase({ ...nuevaClase, aula: e.target.value })}
                    style={inputStyle}
                  />
                </div>

                <div>
                  <label style={labelStyle}>PerÃ­odo</label>
                  <input
                    type="text"
                    placeholder="Ej: 2024-2"
                    value={nuevaClase.periodo}
                    onChange={(e) => setNuevaClase({ ...nuevaClase, periodo: e.target.value })}
                    style={inputStyle}
                  />
                </div>
              </div>

              <div style={{ display: 'flex', gap: '12px', marginTop: '24px' }}>
                <button
                  type="submit"
                  disabled={loading}
                  style={{
                    backgroundColor: loading ? '#9ca3af' : '#10b981',
                    color: 'white',
                    padding: '12px 24px',
                    borderRadius: '8px',
                    border: 'none',
                    fontSize: '15px',
                    fontWeight: '600',
                    cursor: loading ? 'not-allowed' : 'pointer',
                    flex: 1
                  }}
                >
                  {loading ? 'â³ Guardando...' : 'âœ… Guardar Cambios'}
                </button>
                <button
                  type="button"
                  onClick={() => setModalEdicion({ abierto: false, clase: null })}
                  style={{
                    backgroundColor: '#6b7280',
                    color: 'white',
                    padding: '12px 24px',
                    borderRadius: '8px',
                    border: 'none',
                    fontSize: '15px',
                    fontWeight: '600',
                    cursor: 'pointer'
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const labelStyle: React.CSSProperties = {
  display: 'block',
  marginBottom: '8px',
  fontWeight: '600',
  fontSize: '14px',
  color: '#374151'
};

const inputStyle: React.CSSProperties = {
  width: '100%',
  padding: '10px 12px',
  border: '1px solid #d1d5db',
  borderRadius: '8px',
  fontSize: '14px',
  outline: 'none'
};

export default AdminPanel;
