import React, { useState, useEffect, useRef } from 'react';
import { supabase } from '../../supabaseClient';
import {
  crearMensaje,
  getConversaciones,
  getMensajesPorEstudiante,
  marcarConversacionLeida,
  getEstadisticasMensajes,
  Conversacion,
  MensajeConDetalles
} from '../../services/mensajeService';
import { UsuarioConDetalles } from '../../services/authService';

interface Estudiante {
  id: number;
  nombre: string;
  apellido: string;
  grado?: string;
  seccion?: string;
  email?: string;
}

interface MessagingSystemProps {
  usuarioActual: UsuarioConDetalles;
}

const RESPUESTAS_RAPIDAS = [
  'Â¡Excelente trabajo! Sigue asÃ­ ðŸ‘',
  'Necesitamos hablar sobre tu rendimiento acadÃ©mico',
  'Por favor, entrega las tareas pendientes',
  'Felicitaciones por tu dedicaciÃ³n ðŸŽ‰',
  'Tu tutor debe comunicarse conmigo',
  'Recuerda asistir a clases puntualmente'
];

const MessagingSystem: React.FC<MessagingSystemProps> = ({ usuarioActual }) => {
  const [conversaciones, setConversaciones] = useState<Conversacion[]>([]);
  const [conversacionActiva, setConversacionActiva] = useState<number | null>(null);
  const [mensajesActivos, setMensajesActivos] = useState<MensajeConDetalles[]>([]);
  const [estudiantes, setEstudiantes] = useState<Estudiante[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [busqueda, setBusqueda] = useState('');
  const [mostrarNuevoMensaje, setMostrarNuevoMensaje] = useState(false);
  
  const [estadisticas, setEstadisticas] = useState({
    total: 0,
    noLeidos: 0,
    leidos: 0,
    porTipo: {
      general: 0,
      urgente: 0,
      academico: 0,
      conductual: 0,
      felicitacion: 0
    }
  });

  // formulario de mensaje
  const [formMensaje, setFormMensaje] = useState({
    estudiante_id: '',
    asunto: '',
    mensaje: '',
    tipo: 'general' as 'general' | 'urgente' | 'academico' | 'conductual' | 'felicitacion',
    destinatario_tipo: 'tutor' as 'tutor' | 'estudiante'
  });

  const chatEndRef = useRef<HTMLDivElement>(null);
  
  // determinar ID y rol del usuario actual
  const esDocente = usuarioActual.rol === 'docente';
  const esEstudiante = usuarioActual.rol === 'estudiante';
  const usuarioId = usuarioActual.detalles?.id || 1;

  useEffect(() => {
    cargarDatos();
  }, []);

  useEffect(() => {
    if (conversacionActiva) {
      cargarMensajesEstudiante(conversacionActiva);
    }
  }, [conversacionActiva]);

  useEffect(() => {
    scrollToBottom();
  }, [mensajesActivos]);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const cargarDatos = async () => {
    try {
      setLoading(true);
      
      // cargar estudiantes
      const { data: estudiantesData, error: estudiantesError } = await supabase
        .from('Estudiantes')
        .select('id, nombre, apellido, grado, seccion, email')
        .order('apellido', { ascending: true });

      if (estudiantesError) throw estudiantesError;
      setEstudiantes(estudiantesData || []);

      // cargar conversaciones (solo para docentes)
      if (esDocente) {
        const conversacionesData = await getConversaciones(usuarioId);
        setConversaciones(conversacionesData);

        // cargar estadÃ­sticas
        const stats = await getEstadisticasMensajes(usuarioId);
        setEstadisticas(stats);
      } else if (esEstudiante) {
        // para estudiantes, cargar sus propios mensajes
        const mensajesEstudiante = await getMensajesPorEstudiante(usuarioId);
        setMensajesActivos(mensajesEstudiante);
      }

      setError(null);
    } catch (err: any) {
      setError('Error al cargar datos: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const cargarMensajesEstudiante = async (estudianteId: number) => {
    try {
      const mensajes = await getMensajesPorEstudiante(estudianteId);
      setMensajesActivos(mensajes);
      
      // marcar como leÃ­dos
      await marcarConversacionLeida(estudianteId);
      
      // actualizar conversaciones
      cargarDatos();
    } catch (err: any) {
      setError('Error al cargar mensajes: ' + err.message);
    }
  };

  const handleEnviarMensaje = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formMensaje.estudiante_id || !formMensaje.asunto || !formMensaje.mensaje) {
      setError('Por favor completa todos los campos obligatorios');
      return;
    }

    try {
      setLoading(true);

      await crearMensaje({
        remitente_tipo: usuarioActual.rol as any,
        remitente_id: usuarioId,
        destinatario_tipo: formMensaje.destinatario_tipo,
        destinatario_id: undefined, // se enviarÃ­a al tutor o estudiante segÃºn el caso
        estudiante_id: parseInt(formMensaje.estudiante_id),
        asunto: formMensaje.asunto,
        mensaje: formMensaje.mensaje,
        tipo: formMensaje.tipo,
        leido: false
      });

      alert('âœ… Mensaje enviado correctamente');
      
      setFormMensaje({
        estudiante_id: '',
        asunto: '',
        mensaje: '',
        tipo: 'general',
        destinatario_tipo: 'tutor'
      });

      setMostrarNuevoMensaje(false);
      cargarDatos();

      // si estamos en la conversaciÃ³n de este estudiante, recargar mensajes
      if (conversacionActiva === parseInt(formMensaje.estudiante_id)) {
        cargarMensajesEstudiante(conversacionActiva);
      }

    } catch (err: any) {
      setError('Error al enviar mensaje: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRespuestaRapida = (respuesta: string) => {
    setFormMensaje({ ...formMensaje, mensaje: respuesta });
  };

  const getTipoColor = (tipo: string) => {
    switch (tipo) {
      case 'urgente': return { bg: '#fee2e2', text: '#991b1b', icon: 'ðŸš¨' };
      case 'academico': return { bg: '#dbeafe', text: '#1e40af', icon: 'ðŸ“š' };
      case 'conductual': return { bg: '#fef3c7', text: '#92400e', icon: 'âš ï¸' };
      case 'felicitacion': return { bg: '#dcfce7', text: '#166534', icon: 'ðŸŽ‰' };
      default: return { bg: '#f3f4f6', text: '#374151', icon: 'ðŸ’¬' };
    }
  };

  const getTipoBadge = (tipo: string) => {
    const colores = getTipoColor(tipo);
    return (
      <span style={{
        backgroundColor: colores.bg,
        color: colores.text,
        padding: '4px 10px',
        borderRadius: '12px',
        fontSize: '11px',
        fontWeight: '600',
        display: 'inline-flex',
        alignItems: 'center',
        gap: '4px'
      }}>
        {colores.icon} {tipo.toUpperCase()}
      </span>
    );
  };

  const estudiantesFiltrados = estudiantes.filter(est =>
    `${est.nombre} ${est.apellido}`.toLowerCase().includes(busqueda.toLowerCase()) ||
    est.email?.toLowerCase().includes(busqueda.toLowerCase())
  );

  const conversacionesFiltradas = conversaciones.filter(conv =>
    `${conv.estudiante_nombre} ${conv.estudiante_apellido}`.toLowerCase().includes(busqueda.toLowerCase())
  );

  const estudianteActivo = conversacionActiva 
    ? conversaciones.find(c => c.estudiante_id === conversacionActiva)
    : null;

  return (
    <div style={{ height: 'calc(100vh - 80px)', display: 'flex', flexDirection: 'column', backgroundColor: '#f9fafb' }}>
      {/* Header con EstadÃ­sticas */}
      <div style={{ padding: '20px', backgroundColor: 'white', borderBottom: '1px solid #e5e7eb' }}>
        <h1 style={{ fontSize: '28px', fontWeight: '700', color: '#1a1a1a', marginBottom: '16px' }}>
          ðŸ’¬ Sistema de MensajerÃ­a
        </h1>

        {error && (
          <div style={{
            padding: '12px 16px',
            backgroundColor: '#fee2e2',
            border: '1px solid #fecaca',
            color: '#b91c1c',
            borderRadius: '8px',
            marginBottom: '16px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            <span>âš ï¸</span>
            <span>{error}</span>
            <button
              onClick={() => setError(null)}
              style={{
                marginLeft: 'auto',
                background: 'none',
                border: 'none',
                fontSize: '18px',
                cursor: 'pointer',
                color: '#b91c1c'
              }}
            >
              âœ•
            </button>
          </div>
        )}

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '12px' }}>
          <div style={{ backgroundColor: '#ede9fe', padding: '16px', borderRadius: '10px', border: '1px solid #ddd6fe' }}>
            <div style={{ fontSize: '24px', fontWeight: '700', color: '#7c3aed' }}>
              {estadisticas.total}
            </div>
            <div style={{ fontSize: '12px', color: '#6d28d9', fontWeight: '500' }}>Total Mensajes</div>
          </div>

          <div style={{ backgroundColor: '#dbeafe', padding: '16px', borderRadius: '10px', border: '1px solid #bfdbfe' }}>
            <div style={{ fontSize: '24px', fontWeight: '700', color: '#2563eb' }}>
              {conversaciones.length}
            </div>
            <div style={{ fontSize: '12px', color: '#1d4ed8', fontWeight: '500' }}>Conversaciones</div>
          </div>

          <div style={{ backgroundColor: '#fef3c7', padding: '16px', borderRadius: '10px', border: '1px solid #fde68a' }}>
            <div style={{ fontSize: '24px', fontWeight: '700', color: '#d97706' }}>
              {estadisticas.noLeidos}
            </div>
            <div style={{ fontSize: '12px', color: '#b45309', fontWeight: '500' }}>Sin Leer</div>
          </div>

          <div style={{ backgroundColor: '#dcfce7', padding: '16px', borderRadius: '10px', border: '1px solid #a7f3d0' }}>
            <div style={{ fontSize: '24px', fontWeight: '700', color: '#059669' }}>
              {estadisticas.leidos}
            </div>
            <div style={{ fontSize: '12px', color: '#047857', fontWeight: '500' }}>LeÃ­dos</div>
          </div>
        </div>
      </div>

      {/* Layout principal estilo WhatsApp */}
      <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
        {/* Panel izquierdo: Lista de conversaciones */}
        <div style={{
          width: '350px',
          backgroundColor: 'white',
          borderRight: '1px solid #e5e7eb',
          display: 'flex',
          flexDirection: 'column'
        }}>
          {/* BÃºsqueda y botÃ³n nuevo */}
          <div style={{ padding: '16px', borderBottom: '1px solid #e5e7eb' }}>
            <div style={{ marginBottom: '12px' }}>
              <input
                type="text"
                placeholder="ðŸ” Buscar conversaciÃ³n..."
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px'
                }}
              />
            </div>
            <button
              onClick={() => setMostrarNuevoMensaje(true)}
              style={{
                width: '100%',
                backgroundColor: '#3b82f6',
                color: 'white',
                padding: '10px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '14px',
                fontWeight: '600',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px'
              }}
            >
              âœï¸ Nuevo Mensaje
            </button>
          </div>

          {/* Lista de conversaciones */}
          <div style={{ flex: 1, overflowY: 'auto' }}>
            {loading ? (
              <div style={{ padding: '40px', textAlign: 'center', color: '#9ca3af' }}>
                <div style={{ fontSize: '32px', marginBottom: '12px' }}>â³</div>
                <p>Cargando...</p>
              </div>
            ) : conversacionesFiltradas.length === 0 ? (
              <div style={{ padding: '40px', textAlign: 'center', color: '#9ca3af' }}>
                <div style={{ fontSize: '48px', marginBottom: '12px' }}>ðŸ“­</div>
                <p style={{ fontSize: '14px' }}>No hay conversaciones</p>
              </div>
            ) : (
              conversacionesFiltradas.map((conv) => (
                <div
                  key={conv.estudiante_id}
                  onClick={() => setConversacionActiva(conv.estudiante_id)}
                  style={{
                    padding: '16px',
                    borderBottom: '1px solid #f3f4f6',
                    cursor: 'pointer',
                    backgroundColor: conversacionActiva === conv.estudiante_id ? '#f0f9ff' : 'white',
                    transition: 'background-color 0.2s'
                  }}
                  onMouseOver={(e) => {
                    if (conversacionActiva !== conv.estudiante_id) {
                      e.currentTarget.style.backgroundColor = '#f9fafb';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (conversacionActiva !== conv.estudiante_id) {
                      e.currentTarget.style.backgroundColor = 'white';
                    }
                  }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                    <div style={{
                      width: '48px',
                      height: '48px',
                      borderRadius: '50%',
                      backgroundColor: '#e0e7ff',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '20px',
                      fontWeight: '600',
                      color: '#4f46e5',
                      flexShrink: 0
                    }}>
                      {conv.estudiante_nombre.charAt(0)}{conv.estudiante_apellido.charAt(0)}
                    </div>
                    <div style={{ flex: 1, minWidth: 0 }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '4px' }}>
                        <div style={{ fontWeight: '600', color: '#1f2937', fontSize: '14px' }}>
                          {conv.estudiante_apellido} {conv.estudiante_nombre}
                        </div>
                        {conv.mensajes_no_leidos > 0 && (
                          <div style={{
                            backgroundColor: '#3b82f6',
                            color: 'white',
                            borderRadius: '12px',
                            padding: '2px 8px',
                            fontSize: '11px',
                            fontWeight: '700'
                          }}>
                            {conv.mensajes_no_leidos}
                          </div>
                        )}
                      </div>
                      <div style={{ fontSize: '11px', color: '#9ca3af', marginBottom: '4px' }}>
                        {conv.estudiante_grado}{conv.estudiante_seccion}
                      </div>
                      <div style={{
                        fontSize: '13px',
                        color: '#6b7280',
                        whiteSpace: 'nowrap',
                        overflow: 'hidden',
                        textOverflow: 'ellipsis'
                      }}>
                        {conv.ultimo_mensaje}
                      </div>
                      <div style={{ marginTop: '4px' }}>
                        {getTipoBadge(conv.ultimo_mensaje_tipo)}
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Panel derecho: Chat activo o formulario nuevo mensaje */}
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', backgroundColor: '#f9fafb' }}>
          {mostrarNuevoMensaje ? (
            // Formulario nuevo mensaje
            <div style={{ flex: 1, padding: '24px', overflowY: 'auto' }}>
              <div style={{ maxWidth: '700px', margin: '0 auto' }}>
                <div style={{
                  backgroundColor: 'white',
                  borderRadius: '12px',
                  padding: '24px',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                  border: '1px solid #e5e7eb'
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
                    <h2 style={{ fontSize: '20px', fontWeight: '600', color: '#374151' }}>
                      âœï¸ Nuevo Mensaje
                    </h2>
                    <button
                      onClick={() => setMostrarNuevoMensaje(false)}
                      style={{
                        background: 'none',
                        border: 'none',
                        fontSize: '24px',
                        cursor: 'pointer',
                        color: '#9ca3af'
                      }}
                    >
                      âœ•
                    </button>
                  </div>

                  <form onSubmit={handleEnviarMensaje}>
                    <div style={{ marginBottom: '20px' }}>
                      <label style={labelStyle}>Estudiante *</label>
                      <select
                        value={formMensaje.estudiante_id}
                        onChange={(e) => setFormMensaje({ ...formMensaje, estudiante_id: e.target.value })}
                        style={inputStyle}
                        required
                      >
                        <option value="">Seleccionar estudiante</option>
                        {estudiantesFiltrados.map(est => (
                          <option key={est.id} value={est.id}>
                            {est.apellido} {est.nombre} - {est.grado}{est.seccion}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', marginBottom: '20px' }}>
                      <div>
                        <label style={labelStyle}>Destinatario *</label>
                        <select
                          value={formMensaje.destinatario_tipo}
                          onChange={(e) => setFormMensaje({ ...formMensaje, destinatario_tipo: e.target.value as any })}
                          style={inputStyle}
                        >
                          <option value="tutor">ðŸ‘¨â€ðŸ‘©â€ðŸ‘¦ Tutor/Apoderado</option>
                          <option value="estudiante">ðŸŽ“ Estudiante</option>
                        </select>
                      </div>

                      <div>
                        <label style={labelStyle}>Tipo de Mensaje *</label>
                        <select
                          value={formMensaje.tipo}
                          onChange={(e) => setFormMensaje({ ...formMensaje, tipo: e.target.value as any })}
                          style={inputStyle}
                        >
                          <option value="general">ðŸ’¬ General</option>
                          <option value="academico">ðŸ“š AcadÃ©mico</option>
                          <option value="conductual">âš ï¸ Conductual</option>
                          <option value="urgente">ðŸš¨ Urgente</option>
                          <option value="felicitacion">ðŸŽ‰ FelicitaciÃ³n</option>
                        </select>
                      </div>
                    </div>

                    <div style={{ marginBottom: '20px' }}>
                      <label style={labelStyle}>Asunto *</label>
                      <input
                        type="text"
                        value={formMensaje.asunto}
                        onChange={(e) => setFormMensaje({ ...formMensaje, asunto: e.target.value })}
                        style={inputStyle}
                        placeholder="Ej: RevisiÃ³n de calificaciones"
                        required
                      />
                    </div>

                    <div style={{ marginBottom: '16px' }}>
                      <label style={labelStyle}>Mensaje *</label>
                      <textarea
                        value={formMensaje.mensaje}
                        onChange={(e) => setFormMensaje({ ...formMensaje, mensaje: e.target.value })}
                        style={{ ...inputStyle, minHeight: '120px', resize: 'vertical' }}
                        placeholder="Escribe tu mensaje aquÃ­..."
                        required
                      />
                    </div>

                    {/* Respuestas rÃ¡pidas */}
                    <div style={{ marginBottom: '20px' }}>
                      <label style={{ ...labelStyle, marginBottom: '8px' }}>âš¡ Respuestas RÃ¡pidas:</label>
                      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                        {RESPUESTAS_RAPIDAS.map((respuesta, idx) => (
                          <button
                            key={idx}
                            type="button"
                            onClick={() => handleRespuestaRapida(respuesta)}
                            style={{
                              backgroundColor: '#f3f4f6',
                              border: '1px solid #d1d5db',
                              padding: '6px 12px',
                              borderRadius: '16px',
                              fontSize: '12px',
                              cursor: 'pointer',
                              transition: 'all 0.2s'
                            }}
                            onMouseOver={(e) => {
                              e.currentTarget.style.backgroundColor = '#e5e7eb';
                              e.currentTarget.style.borderColor = '#9ca3af';
                            }}
                            onMouseOut={(e) => {
                              e.currentTarget.style.backgroundColor = '#f3f4f6';
                              e.currentTarget.style.borderColor = '#d1d5db';
                            }}
                          >
                            {respuesta}
                          </button>
                        ))}
                      </div>
                    </div>

                    <div style={{ display: 'flex', gap: '12px' }}>
                      <button
                        type="submit"
                        disabled={loading}
                        style={{
                          flex: 1,
                          backgroundColor: loading ? '#9ca3af' : '#10b981',
                          color: 'white',
                          padding: '12px',
                          borderRadius: '8px',
                          border: 'none',
                          fontSize: '15px',
                          fontWeight: '600',
                          cursor: loading ? 'not-allowed' : 'pointer'
                        }}
                      >
                        {loading ? 'â³ Enviando...' : 'ðŸ“¤ Enviar Mensaje'}
                      </button>

                      <button
                        type="button"
                        onClick={() => setMostrarNuevoMensaje(false)}
                        style={{
                          backgroundColor: '#6b7280',
                          color: 'white',
                          padding: '12px 24px',
                          borderRadius: '8px',
                          border: 'none',
                          fontSize: '15px',
                          fontWeight: '600',
                          cursor: 'pointer'
                        }}
                      >
                        Cancelar
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          ) : conversacionActiva && estudianteActivo ? (
            // Vista de chat
            <>
              {/* Header del chat */}
              <div style={{
                backgroundColor: 'white',
                padding: '16px 24px',
                borderBottom: '1px solid #e5e7eb',
                display: 'flex',
                alignItems: 'center',
                gap: '12px'
              }}>
                <div style={{
                  width: '48px',
                  height: '48px',
                  borderRadius: '50%',
                  backgroundColor: '#e0e7ff',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '20px',
                  fontWeight: '600',
                  color: '#4f46e5'
                }}>
                  {estudianteActivo.estudiante_nombre.charAt(0)}{estudianteActivo.estudiante_apellido.charAt(0)}
                </div>
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: '600', fontSize: '16px', color: '#1f2937' }}>
                    {estudianteActivo.estudiante_apellido} {estudianteActivo.estudiante_nombre}
                  </div>
                  <div style={{ fontSize: '13px', color: '#6b7280' }}>
                    {estudianteActivo.estudiante_grado}{estudianteActivo.estudiante_seccion} â€¢ {estudianteActivo.total_mensajes} mensajes
                  </div>
                </div>
                <button
                  onClick={() => setConversacionActiva(null)}
                  style={{
                    background: 'none',
                    border: 'none',
                    fontSize: '24px',
                    cursor: 'pointer',
                    color: '#9ca3af'
                  }}
                >
                  âœ•
                </button>
              </div>

              {/* Mensajes */}
              <div style={{
                flex: 1,
                overflowY: 'auto',
                padding: '24px',
                display: 'flex',
                flexDirection: 'column',
                gap: '16px'
              }}>
                {mensajesActivos.length === 0 ? (
                  <div style={{ textAlign: 'center', padding: '60px 20px', color: '#9ca3af' }}>
                    <div style={{ fontSize: '64px', marginBottom: '16px' }}>ðŸ’¬</div>
                    <p style={{ fontSize: '16px', fontWeight: '500', color: '#6b7280' }}>
                      No hay mensajes en esta conversaciÃ³n
                    </p>
                    <p style={{ fontSize: '14px', marginTop: '8px' }}>
                      Â¡EnvÃ­a el primer mensaje!
                    </p>
                  </div>
                ) : (
                  mensajesActivos.map((mensaje) => (
                    <div
                      key={mensaje.id}
                      style={{
                        display: 'flex',
                        justifyContent: 'flex-end',
                        marginBottom: '8px'
                      }}
                    >
                      <div style={{
                        maxWidth: '70%',
                        backgroundColor: '#3b82f6',
                        color: 'white',
                        padding: '12px 16px',
                        borderRadius: '16px 16px 4px 16px',
                        boxShadow: '0 2px 4px rgba(59, 130, 246, 0.3)'
                      }}>
