// Contenido para src/components/Sidebar/Sidebar.tsx

import React from 'react';
// Usamos NavLink en lugar de Link para que sepa cu치l es la "p치gina activa"
import { NavLink } from 'react-router-dom'; 

// Definimos los props que recibir치 (el rol del usuario actual)
type SidebarProps = {
  role: string;
};

// Definimos un tipo para los enlaces
type NavItem = {
  path: string;
  text: string;
  icon: string;
};

const Sidebar: React.FC<SidebarProps> = ({ role }) => {
  let navItems: NavItem[] = [];

  // Esta l칩gica es la misma de tu index.html, pero adaptada a React
  switch (role) {
    case 'student':
      navItems = [
        { text: 'Mi Perfil', icon: '游녻', path: '/dashboard' },
        { text: 'Mis Notas', icon: '游늵', path: '/grades' },
        { text: 'Mi Asistencia', icon: '游늰', path: '/attendance' },
        { text: 'Mensajes', icon: '游눫', path: '/messages' },
        { text: 'Configuraci칩n', icon: '丘뙖잺', path: '/settings' }
      ];
      break;
    case 'psychologist':
      navItems = [
        { text: 'Panel Principal', icon: '游', path: '/dashboard' },
        { text: 'Estudiantes en Riesgo', icon: '丘멆잺', path: '/risk' },
        { text: 'Agenda', icon: '游늰', path: '/schedule' },
        { text: 'Reportes', icon: '游늳', path: '/reports' },
        { text: 'Mensajer칤a', icon: '游눫', path: '/messages' }
      ];
      break;
    case 'director':
      navItems = [
        { text: 'Panel Directivo', icon: '游녮', path: '/dashboard' },
        { text: 'Indicadores', icon: '游늵', path: '/metrics' },
        { text: 'Reportes Ejecutivos', icon: '游늳', path: '/reports' },
        { text: 'Mensajer칤a', icon: '游눫', path: '/messages' }
      ];
      break;
    default: // 'teacher'
      navItems = [
        { text: 'Dashboard', icon: '游늵', path: '/dashboard' },
        { text: 'Asistencia', icon: '游낆', path: '/attendance' },
        { text: 'Reportes', icon: '游늳', path: '/reports' },
        { text: 'Estudiantes', icon: '游논', path: '/students' },
        { text: 'Mensajer칤a', icon: '游눫', path: '/messages' },
        { text: 'Configuraci칩n', icon: '丘뙖잺', path: '/settings' }
      ];
  }

  return (
    <nav className="sidebar">
      {navItems.map((item) => (
        <NavLink 
          to={item.path} 
          key={item.path}
          // Esto hace que el link activo tenga la clase 'active'
          className={({ isActive }) => "nav-item" + (isActive ? " active" : "")}
        >
          <i>{item.icon}</i>
          {item.text}
        </NavLink>
      ))}
    </nav>
  );
};

export default Sidebar;
