import React, { useState, useEffect } from 'react';
import { getAllStudents } from '../../services/studentService';
import { 
  getAsistenciaByFecha, 
  upsertAsistencia
} from '../../services/asistenciaService';
import { AsistenciaConDetalles } from '../../types/database';
import { Student } from '../../types';

const AttendanceSystem: React.FC = () => {
  const [students, setStudents] = useState<Student[]>([]);
  const [attendanceRecords, setAttendanceRecords] = useState<AsistenciaConDetalles[]>([]);
  const [selectedDate, setSelectedDate] = useState<string>(
    new Date().toISOString().split('T')[0]
  );
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // formulario
  const [formData, setFormData] = useState({
    date: new Date().toISOString().split('T')[0],
    studentId: '' as string | number,
    status: 'presente' as 'presente' | 'ausente' | 'tarde' | 'justificado',
    notes: '',
  });

  // cargar estudiantes al montar el componente
  useEffect(() => {
    loadStudents();
  }, []);

  // cargar asistencias cuando cambia la fecha
  useEffect(() => {
    loadAttendance(selectedDate);
  }, [selectedDate]);

  const loadStudents = async () => {
    try {
      setLoading(true);
      const data = await getAllStudents();
      setStudents(data);
    } catch (err: any) {
      setError('Error al cargar estudiantes: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const loadAttendance = async (date: string) => {
    try {
      setLoading(true);
      const data = await getAsistenciaByFecha(date);
      setAttendanceRecords(data);
      setError(null);
    } catch (err: any) {
      setError('Error al cargar asistencias: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    
    if (!formData.studentId) {
      setError('Por favor selecciona un estudiante');
      return;
    }

    try {
      setLoading(true);
      await upsertAsistencia({
        estudiante_id: Number(formData.studentId),
        fecha: formData.date,
        estado: formData.status,
        observaciones: formData.notes || undefined,
      });

      // recargar la lista de asistencias
      await loadAttendance(selectedDate);
      
      // limpiar formulario
      setFormData({
        ...formData,
        studentId: '',
        notes: '',
      });
      
      setError(null);
      alert('Asistencia registrada correctamente');
    } catch (err: any) {
      setError('Error al registrar asistencia: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'presente':
        return 'status-badge success';
      case 'tarde':
        return 'status-badge warning';
      case 'ausente':
        return 'status-badge error';
      case 'justificado':
        return 'status-badge info';
      default:
        return 'status-badge';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'presente':
        return 'Presente';
      case 'tarde':
        return 'Tardanza';
      case 'ausente':
        return 'Ausente';
      case 'justificado':
        return 'Justificado';
      default:
        return status;
    }
  };

  return (
    <>
      <h1 className="page-title">Control de Asistencia</h1>

      {error && (
        <div style={{ 
          padding: '10px', 
          backgroundColor: '#ffebee', 
          color: '#c62828',
          borderRadius: '4px',
          marginBottom: '20px'
        }}>
          {error}
        </div>
      )}
                
      <div className="section">
        <h2>Registrar Asistencia</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="attendanceDate">Fecha</label>
              <input 
                type="date" 
                id="attendanceDate" 
                value={formData.date}
                onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="studentSelect">Estudiante</label>
              <select 
                id="studentSelect" 
                value={formData.studentId}
                onChange={(e) => setFormData({ ...formData, studentId: e.target.value })}
                required
                disabled={loading}
              >
                <option value="">Seleccionar estudiante</option>
                {students.map((student) => (
                  <option key={student.id} value={student.id}>
                    {student.lastName} {student.name} - {student.grade}{student.section}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="attendanceStatus">Estado</label>
              <select 
                id="attendanceStatus" 
                value={formData.status}
                onChange={(e) => setFormData({ 
                  ...formData, 
                  status: e.target.value as any 
                })}
              >
                <option value="presente">Presente</option>
                <option value="ausente">Ausente</option>
                <option value="tarde">Tardanza</option>
                <option value="justificado">Justificado</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="attendanceNotes">Notas (opcional)</label>
              <input 
                type="text" 
                id="attendanceNotes" 
                placeholder="Notas adicionales"
                value={formData.notes}
                onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
              />
            </div>
          </div>
          <button type="submit" className="btn" disabled={loading}>
            {loading ? 'Guardando...' : 'Registrar Asistencia'}
          </button>
        </form>
      </div>

      <div className="section">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2>Lista de Asistencia</h2>
          <div className="form-group" style={{ marginBottom: 0 }}>
            <label htmlFor="filterDate" style={{ marginRight: '10px' }}>Fecha:</label>
            <input 
              type="date" 
              id="filterDate" 
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            />
          </div>
        </div>

        {loading ? (
          <p>Cargando...</p>
        ) : attendanceRecords.length === 0 ? (
          <p>No hay registros de asistencia para esta fecha.</p>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>Estudiante</th>
                <th>Grado/Secci√≥n</th>
                <th>Estado</th>
                <th>Notas</th>
              </tr>
            </thead>
            <tbody>
              {attendanceRecords.map((record) => (
                <tr key={record.id}>
                  <td>
                    {record.estudiante?.apellido} {record.estudiante?.nombre}
                  </td>
                  <td>
                    {record.estudiante?.grado}{record.estudiante?.seccion}
                  </td>
                  <td>
                    <span className={getStatusBadgeClass(record.estado)}>
                      {getStatusText(record.estado)}
                    </span>
                  </td>
                  <td>{record.observaciones || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
};

export default AttendanceSystem;
