import { supabase } from '../supabaseClient';
import { Student } from '../types';

// obtener todos los estudiantes
export const getAllStudents = async (): Promise<Student[]> => {
  const { data, error } = await supabase
    .from('students')
    .select('*')
    .order('last_name', { ascending: true });

  if (error) throw error;
  
  // mapear a la estructura esperada por el frontend
  return (data || []).map(student => ({
    id: student.id,
    name: student.name,
    lastName: student.last_name,
    email: student.email,
    grade: student.grade,
    section: student.section,
    attendance: [],
    grades: [],
  }));
};

// obtener estudiante por ID
export const getStudentById = async (id: string): Promise<Student | null> => {
  const { data, error } = await supabase
    .from('students')
    .select('*')
    .eq('id', id)
    .single();

  if (error) throw error;
  if (!data) return null;

  return {
    id: data.id,
    name: data.name,
    lastName: data.last_name,
    email: data.email,
    grade: data.grade,
    section: data.section,
    attendance: [],
    grades: [],
  };
};

// obtener estudiantes por grado y sección
export const getStudentsByGradeAndSection = async (
  grade: string,
  section: string
): Promise<Student[]> => {
  const { data, error } = await supabase
    .from('students')
    .select('*')
    .eq('grade', grade)
    .eq('section', section)
    .order('last_name', { ascending: true });

  if (error) throw error;

  return (data || []).map(student => ({
    id: student.id,
    name: student.name,
    lastName: student.last_name,
    email: student.email,
    grade: student.grade,
    section: student.section,
    attendance: [],
    grades: [],
  }));
};

// crear estudiante
export const createStudent = async (
  student: Omit<Student, 'id' | 'attendance' | 'grades'>
): Promise<Student> => {
  const { data, error } = await supabase
    .from('students')
    .insert({
      name: student.name,
      last_name: student.lastName,
      email: student.email,
      grade: student.grade,
      section: student.section,
    })
    .select()
    .single();

  if (error) throw error;

  return {
    id: data.id,
    name: data.name,
    lastName: data.last_name,
    email: data.email,
    grade: data.grade,
    section: data.section,
    attendance: [],
    grades: [],
  };
};

// actualizar estudiante
export const updateStudent = async (
  id: string,
  student: Partial<Omit<Student, 'id' | 'attendance' | 'grades'>>
): Promise<Student> => {
  const updateData: any = {};
  if (student.name) updateData.name = student.name;
  if (student.lastName) updateData.last_name = student.lastName;
  if (student.email) updateData.email = student.email;
  if (student.grade) updateData.grade = student.grade;
  if (student.section) updateData.section = student.section;

  const { data, error } = await supabase
    .from('students')
    .update(updateData)
    .eq('id', id)
    .select()
    .single();

  if (error) throw error;

  return {
    id: data.id,
    name: data.name,
    lastName: data.last_name,
    email: data.email,
    grade: data.grade,
    section: data.section,
    attendance: [],
    grades: [],
  };
};

// eliminar estudiante
export const deleteStudent = async (id: string): Promise<void> => {
  const { error } = await supabase
    .from('students')
    .delete()
    .eq('id', id);

  if (error) throw error;
};
